catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    // Hide any previous error message on the email field
    g_form.hideFieldMsg('email', true);

    // Instantiate GlideAjax for the AjaxFunctions2 Script Include
    var ga = new GlideAjax('AjaxFunctions2');
    ga.addParam('sysparm_name', 'checkUserRequest');
    ga.addParam('sysparm_email', g_form.getValue('email'));
    ga.addParam('sysparm_vaga', g_form.getValue('question_event'));

// alert(g_form.getValue('email') + ',' + g_form.getValue('question_event'));

    // Fetch the answer and process it
    ga.getXMLAnswer(function(answer) {
		// alert(answer);
        if (answer && answer !== 'none') {
alert(
    'O e-mail ' + g_form.getValue('email') + ' já está registrado para o evento: ' + g_form.getDisplayValue('question_event') + 
    '.\n\nSubmeter um novo registro para este evento não é permitido.'
);

            // Optionally, clear the email field if needed
            // g_form.clearValue('email');
			// g_form.clearValue('question_full_name');
			g_form.clearValue('question_event');
        }
    });
}
