sys_script_include.script:
var AjaxFunctions2 = Class.create();
AjaxFunctions2.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    checkUserRequest: function() {
        var email = this.getParameter('sysparm_email');
        var vaga = this.getParameter('sysparm_vaga'); // Assuming this is a name, confirm the field type and adjust if needed

        var requests = [];

        var ritm = new GlideRecord('x_1488662_event_re_registration');
        ritm.addQuery('email', email);
        ritm.addQuery('event', vaga); // Ensure event.name is indexed, or use event Sys ID if available
        ritm.query();

        while (ritm.next()) {
            requests.push(ritm.number.toString());
        }

        if (requests.length > 0) {
            return requests.join(',');
        } else {
            return 'none';
        }
    },

    type: 'AjaxFunctions2'
});
