sys_ui_action.script:
(function() {
    var loggedInUser = gs.getUserID(); // Get the logged-in user
    var approverFound = false;  // Flag to track if the user is found in the approvers list


    var sysApp = new GlideRecord('sysapproval_approver');
    sysApp.addQuery('sysapproval', current.sys_id);  // Query for the current approval record
    sysApp.query();


    // Check if the logged-in user is in the sysapproval_approver list
    while (sysApp.next()) {
        if (sysApp.approver == loggedInUser) {
            approverFound = true;  // Mark the user as found
            sysApp.state = "approved";  // Approve the record
            sysApp.update();  // Save the approval state
            action.setRedirectURL(current);  // Redirect to the current record
            break;
        }
    }


    // If the user was not found in the approvers list, show a message and abort
    if (!approverFound) {
        gs.addErrorMessage('You are not listed as an approver for this record.');
        action.setAbortAction(true);  // Abort the action to prevent any further changes
        action.setRedirectURL(current);
    }


})();
